    private List<String> policeDepartments;

    private Counties(List<String> policeDepartments) {
        this.policeDepartments = policeDepartments;
    }

    public List<String> getPD() {
        return policeDepartments;
    }

    public static String findCountyName(String pdName) {
        for (Counties county : Counties.values()) {
            if (county.getPD().contains(pdName)) {
                return county.name();
            }
        }
        return "County not found";
    }

